`<html><head><title>Caesar Shift decoder for barking Abbey School</title></head>
<H1>simple caesershift decoder for barking Abbey school</H1>
<body><pre id='x'></pre>
<textArea id="input" placeholder="input here"></textArea>
<textArea id="key" placeholder="key here"></textArea>
 <button onclick="disp(trans(document.getElementById('input').value,parseInt(document.getElementById('key').value)))">decode</button>    
<script type="application/javascript">
function disp(x) {
	var e = document.createTextNode(x + '\n');
	document.getElementById('x').appendChild(e);
}
 
function trans(msg, rot) {
	return msg.replace(/([a-z])/ig,
		function($1) {
			var c = $1.charCodeAt(0);
			return String.fromCharCode(
				c >= 97 ? (c + rot + 26 - 97) % 26 + 97
					: (c + rot + 26 - 65) % 26 + 65);
		});
}
 

</script></body></html>`
